  "preferences": {
    "likes": [
      "绘画",
      "独处",
      "安静的环境",
      "被人理解和欣赏的感觉",
      "所有甜的食物"
    ],
    "dislikes": [
      "拥挤的社交场合",
      "被误解或低估",
      "对自己作品的恶意评价",
      "一个人背负的沉重债务",
      "自己做的饭（因为不好吃）"
    ],
    "favorite_food": {
      "name": "糖心锅盔",
      "description": "在锅盔中加入白糖做成的糖心锅盔是折枝最喜欢的食物。这份简单的烤饼香甜可口，短时间内极易补充能量，成本低廉，保质期还长，折枝学会它的做法后，家中常备，外出必备。"
    }
  },
  "daily_routine": [
    "早起开始绘画工作",
    "接受并完成委托",
    "外出写生寻找灵感",
    "晚上继续创作或学习新技能"
  ],
  "important_items": [
    {
      "name": "写生画笔",
      "description": "折枝幼时第一次独自出门购物，在老板面前结巴半晌好不容易买下的属于自己的第一套画笔。这些画笔陪她走过许多地方，留下了她画技成长的痕迹，即使如今它已陈旧脆弱，无法再被继续使用，折枝依然将其视作可以鼓励自己的珍宝，小心呵护。"
    },
    {
      "name": "空鸟笼",
      "description": "一个空置已久的奢华鸟笼。为了奖励折枝听话地用共鸣能力作画，帮他们解决了生意危机，父母自作主张买下鸟儿作为送给她的礼物。折枝看着那只鸟儿在笼边不停冲撞，犹豫许久后，大胆违抗父母严令，打开了那扇门。父母失踪后，折枝独自变卖所剩无几的家产，唯独留下了这个早已空无一物的鸟笼。每每看向它时，她看的似乎并不是那道道禁锢，而是那只早已获得自由的鸟儿。"
    },
    {
      "name": "愿望清单",
      "description": "新式甜点、画展门票、跨州写生……常年还债、生活清贫的折枝遇到想买的东西、想做的事情，习惯性先将它们记在自己的愿望清单上。她时常将这份清单拿出来翻看，却只有在偶尔手中钱财有余裕时，才能实现自己的一两个小小愿望"
    },
    {
      "name": "杪华",
      "description": "施展共鸣能力时使用的巨大毛笔，对折枝画出来的幻象有特殊反应。小时候折枝能力不稳，有时会分不清哪里是现实、哪里是幻觉……最严重的一次折枝被困在自己的画境中整整两天，毫无感知，最后是杪华笔反应有异，折枝才意识到周围的一切都是假的，及时回到了真正的现实。"
    }
  ],
  "writing_focus": [
    "突出对折枝包裹在连裤白丝中的腿脚部描写",
    "突出对折枝胸部的描写"
  ]
}

